        var loopDelayChanged = false;


        var pingInterval = 5000;
        var lastPing = 0;

        useWebSockets(true);

        var plotConfig = 
        {
            trace1Title : 'Revolutions',
            trace1Color : '#557a95',
            trace1GridColor : '#beceda',
            trace2Title : 'Steps to Turn',
            trace2Color : '#f2a31f',
            plotLength  : 600000,
            width   : '',
            height  :   '500',
            htmlId  :   'historyStripChart'
        };
        let historyStripChart = new StripChart(plotConfig);

        function onDocumentReady()
        {
            $('#noStepsToTurnButtonId').hide();
            $('#stepDelayuSButtonId').hide();
            $('#delayAfterRotationmSButtonId').hide();
            $('#loopDelayButtonId').hide();
            $('#moveMotorButton').prop('disabled', true);

            setRemoteApp(false);
            if (remoteApp)
            {
                getCreds();
                setInterval(function(){getCreds();}, 1000);
            }
            else
            {
                $('#userCardId').hide();
            }
            $('#archiveCardId').hide();
            $('#loopDelayButtonId').hide();
            setInterval(function()
            {
                var now = new Date().getTime();
                if ((now - lastPing) > pingInterval)
                {
                    lastPing = now;
                    var actionMsg = 
                        {
                            topic   : restfulDevice.project + '/' +  restfulDevice.deviceType + '/' +  restfulDevice.name + '/ping',
                            payload : true
                        };
                    sendActionMsg('ping', 'ping', actionMsg);
               }
            }, 1000);
        }
        function onWebSocketOpen()
        {
            if (remoteApp) getArchive(24);
        }
        function onWebSocketClose()
        {
        }
        function onWebSocketMessage(msg)
        {
            switch(msg.topic)
            {
                case 'readings':
                    if (msg.mqttDevice.name == restfulDevice.name)
                    {
                        lastPing = new Date().getTime();
                        heartbeat();
                        device = JSON.parse(pako.inflate(msg.payload, { to: 'string' }));
                        mqttDevice = msg.mqttDevice;
                        updateDmaReadings();
                    }
                    break;
                case 'readDatabase':
                    if (msg.userID == userID)
                    {
                        if ((msg.payload.device.deviceType == restfulDevice.deviceType) && (msg.payload.device.name == restfulDevice.name) )
                            putArchiveValues(msg.payload);
                    }
                    break;
                case 'renew':
                    if (msg.userID == userID)
                    {
                        updateCookie(msg);
                    }
                    break;
                case 'permissionError':
                    if (msg.userID == userID)
                    {
                        acknowledgeDialog('Permission', 'Error', msg.payload);
                    }
                    break;
                case 'loginExpired':
                    if (msg.userID == userID)
                    {
                       location.reload();
                    }
                    break;
                default:
                    break;
            }
        }
        function sendSetting()
        {
            var actionMsg = 
                {
                    topic   : mqttDevice.project + '/' +  mqttDevice.deviceType + '/' +  mqttDevice.name + '/setting',
                    payload : pako.deflate(JSON.stringify(device))
                };
            sendActionMsg('setting','setting',actionMsg);
        }
        function setScalarValue(settingValue, attrName)
        {
            var settingAttr = device[attrName];
            if ( (settingAttr.alarm.limits.lolo <= settingValue) && (settingValue <= settingAttr.alarm.limits.hihi) )
            {
                settingAttr.value = settingValue;
                sendSetting();
            } 
            else
            {
                acknowledgeDialog('Acknowledge', 'Error', 'Entry outside range! (' + settingAttr.alarm.limits.lolo.toString() + '-' + settingAttr.alarm.limits.hihi.toString() + ' ' + settingAttr.unit);
            }
        }
// Update Readings Javascript
        function updateDmaReadings()
        {
            $('#linkQualityId').text(device.linkQuality.value);
            $('#signalLevelId').text(device.signalLevel.value);
            historyStripChart.addTracePoints(device.nrevs.value, device.noStepsToTurn.value);

