        var remoteApp = true;
        var ws;
        var userID = -1;
        var useWebSocketsFlag = true;
        var project = '{{{payload.project}}}';
        var heartBeatImage = false;
        var wsUri = '';
        var wsConnected = false;

        function setRemoteApp(remoteAppSet)
        {
            remoteApp  = remoteAppSet;
        }
        function webSocketConnected()
        {
            return wsConnected;
        }
        function useWebSockets(webSocketsFlag)
        {
            useWebSocketsFlag = webSocketsFlag;
        }
        function wsConnectC()
        {
            if (!useWebSocketsFlag)
            {
                return;
            }
            if (wsUri.length < 1)
            {
                var uri = window.location.href.split('://');
                var wslead = 'ws://';
                if (uri[0] == 'https') wslead = 'wss://';
                var questionLocation = uri[1].indexOf('?');
                if (questionLocation >= 0)
                {
                    uri[1] = uri[1].substring(0,questionLocation);
//                    console.log(uri[1]);
                }
                if (uri[1].indexOf('/') < (uri[1].length - 1))
                {
                    wsUri = wslead + uri[1] + '/websocket';
                }
                else
                {
                    wsUri = wslead + uri[1] + 'websocket';
                }
            }
            ws = new WebSocket(wsUri);
            ws.onmessage = function(event)
            {
                onWebSocketMessage(JSON.parse(event.data));

            };
            ws.onopen = function()
            {
                console.log("Websocket connected");
                wsConnected = true;
                $('#webSocketStatus').html('Open');
                $('#reOpenWebSocketButton').hide();
                $('#webSocketStatusRow').css('padding-bottom','0px');
                $('#webSocketStatusCard').hide();
                onWebSocketOpen();
            };
            ws.onclose = function()
            {
                console.log("Websocket closed");
                wsConnected = false;
                $('#webSocketStatus').html('Closed');
                $('#reOpenWebSocketButton').show();
                $('#webSocketStatusCard').show();
                $('#webSocketStatusRow').css('padding-bottom','25px');
                onWebSocketClose();
                wsConnectC();
            };
        }

        function getRandomInt(max)
        {
          return Math.floor(Math.random() * Math.floor(max));
        }
        $( document ).ready(function()
        {
            userID  = {{{payload.userID}}};
            if (!useWebSocketsFlag)
            {
                $('#webSocketStatusRow').css('padding-bottom','0px');
                $('#webSocketStatusCard').hide();
            }
            wsConnectC();
            onDocumentReady();
       });
